            $this->alreadyInValidation = true;
            $retval = null;

            $failureMap = array();


            if (($retval = ProfesoresPeer::doValidate($this, $columns)) !== true) {
                $failureMap = array_merge($failureMap, $retval);
            }


                if ($this->collArchivos !== null) {
